import React, { Component } from 'react';
import { graphql, compose} from 'react-apollo'
import { UPDATE_TEAM_MUTATION, GET_TEAMS_QUERY, GET_TEAM_QUERY } from '../queries/queries'

class UpdateTeam extends Component {


    state = {
        name: this.props.name || "none",
        founded: this.props.name || 0,
        teams:[]
    }
   
    

    handleChange = (event) => {
        let { name, value, type} = event.target
        if (type==="number") value = Number(value)
        this.setState({
            [name]: value
        })
    }

    getTeamDetails = () => {
    
        let selectedTeam = this.props.GET_TEAMS_QUERY.teams.find(team => {
            console.log(team.id + " " + this.state.teamId)
            return team.id === this.state.teamId
        })
        console.log(selectedTeam)
        if(selectedTeam){
            this.setState({
                name: selectedTeam.name,
                founded: selectedTeam.name
            })
        }
        

    }


    componentDidMount(){
        let data = this.props.GET_TEAMS_QUERY;
        if(data.loading){
            console.log("Loading teams")
        } else {
            if (!this.state.teams) this.setState({teams:data.teams});
            console.log(this.state.teams)
        }
    }

    showTeams() {
        let data = this.props.GET_TEAMS_QUERY;
        if (data.loading) {
            console.log("Loading teams")
        } else {
        return this.props.GET_TEAMS_QUERY.teams.map(team => {
                return (<option name="teamId" key={team.id} value={team.id}>{team.name}</option>)
            })
        }
    }
    

    submitForm = (event) => {
        event.preventDefault();
        this.props.UPDATE_TEAM_MUTATION({
            variables: {
                name: this.state.name,
                founded: this.state.founded
            }
            , refetchQueries: [{ query: GET_TEAMS_QUERY }]
        })
    }


    render() {
        return (
        <form onSubmit={this.submitForm}>

            <div className="field">
                <label>Team</label>
                <select onChange={(e) => { 
                    this.setState({ 
                        teamId: e.target.value,
                    }, this.getTeamDetails()) 
        
                }}>
                   {this.showTeams()}
                </select>
            </div>


            <div className="field">
              <label>Team Name:</label>
              <input
                name="name"
                type="text"
                onChange={this.handleChange}
              />
            </div>
            <div className="field">
              <label>Founded:</label>
              <input
                name="founded"
                type="number"

                onChange={this.handleChange}
              />
            </div>
            <button>Add</button>
        </form>
        );
    }
}

export default compose(
    graphql(GET_TEAMS_QUERY, { name: "GET_TEAMS_QUERY" }),
    graphql(UPDATE_TEAM_MUTATION, { name: "UPDATE_TEAM_MUTATION" }),
    graphql(GET_TEAM_QUERY, { name: "GET_TEAM_QUERY" })
)(UpdateTeam);
