import React, { Component } from 'react';
import { gql } from 'apollo-boost';
import {graphql, compose} from 'react-apollo'


const GET_TEAMS_QUERY = gql`
    {
        teams{
            name
            id
        }
    }
`
const CREATE_DRIVER_MUTATION = gql`
    mutation($firstName: String!, $lastName: String!, $nationality:String!, $teamId:ID!) {
        addDriver(firstName: $firstName, lastName: $lastName, nationality: $nationality, teamId:$teamId) {
            firstName
            lastName
            nationality
            team
        }
    }
`
class AddDriver extends Component {

    state = {
        firstName: '',
        lastName: '',
        nationality:'',
        teamId:''
    }

    showTeams(){
        let data = this.props.GET_TEAMS_QUERY
        if(data.loading){
            return (<option disabled>Loading Teams...</option>)
        } else {
            return data.teams.map(team => {
                return (<option name="teamId" key={team.id}  value={team.id}>{team.name}</option>)
            })
        }
    }

    handleChange = ((event) =>{
        console.log(event.target.name)
        const{name, value} = event.target
        this.setState({[name]: value})
    });

    submitForm = (event) =>{
        event.preventDefault();
        console.log(this.state.teamId)
        this.props.CREATE_DRIVER_MUTATION({
            variables:{
                firstName: this.state.firstName,
                lastName: this.state.lastName,
                nationality: this.state.nationality,
                teamId: this.state.teamId
            }
        })  
    }


    render() {
        return (
          <form id="add-driver"onSubmit= {this.submitForm}>
            <div className="field">
              <label>First Name:</label>
                <input type="text" name="firstName" onChange={this.handleChange}/>
            </div>
            <div className="field">
                <label>Last Name:</label>
                    <input name="lastName" type="text" onChange={this.handleChange}/>
            </div>
            <div className="field">
                <label>Nationality:</label>
                    <input name="nationality" type="text" onChange={this.handleChange}/>
            </div>
            <div className="field">
                <label>Team</label>
                    <select onChange={ (e) => {this.setState({teamId:e.target.value})}}>
                    {this.showTeams()}
                </select>
            </div>
            <button>Add</button>
          </form>
        );
    }
}

export default compose(
    graphql(GET_TEAMS_QUERY, {name:"GET_TEAMS_QUERY"}),
    graphql(CREATE_DRIVER_MUTATION, {name:"CREATE_DRIVER_MUTATION"}),
)(AddDriver);
