import React, { Component } from 'react';
import { graphql } from 'react-apollo';
import {GET_DRIVER_QUERY} from '../queries/queries';


class DriverDetails extends Component {

    displayDriverDetails= () => {
        const { driver } = this.props.data;
        if (driver) {
            

            const { drivers } = this.props.data.driver.team
            const filteredDrivers = (drivers.filter( d => {
                if(d!==driver.id) return
            })).map(d => {
                return <li>{d.firstName}</li>
            })
        
            console.log(this.props.data.driver)
            return (
                <div>
                    <h2>{driver.firstName} {driver.lastName}</h2>
                    <h3>{driver.nationality}</h3>
                    <h4>{driver.team.name}</h4>
                    <p>{filteredDrivers}</p>

                </div>
            );
        };
    };
    render(){
        return (
            <div>
                
                {this.displayDriverDetails()}
            </div>
        )
    }
 
    
};

export default graphql(GET_DRIVER_QUERY, {
    options: (props) => {
        return {
            variables: {
                id: props.driverId
            }
        }
    }
})(DriverDetails);
