import React, { Component } from 'react';
import { gql } from 'apollo-boost';
import {graphql, compose} from 'react-apollo'


const GET_TEAMS_QUERY = gql`
    {
        teams{
            name
            id
        }
    }
`
const CREATE_DRIVER_MUTATION = gql`
    mutation {
        addDriver(firstName: "", lastName: "", nationality: "", teamId:"") {
            name
        }
    }
`
class AddDriver extends Component {

    state = {
        firstName: '',
        lastName: '',
        nationality:'',
        teamId:''
    }

    showTeams(){
        console.log(this.props);
        let data = this.props
        // if(data.loading){
        //     return (<option disabled>Loading Teams...</option>)
        // } else {
        //     return data.teams.map(team => {
        //         return(<option key={team.id} value={team.id}>{team.name}</option>)
        //     })
        // }
    }

    handleChange = ((event) =>{
        const{name, value} = event.target
        this.setState({[name]: value})
        // this.setState[property]
    });

    submitForm = (event) =>{
        console.log("hey")
        event.preventDefault();

    }


    render() {
        return (
          <form id="add-driver"onSubmit= {this.submitForm}>
            <div className="field">
              <label>First Name:</label>
                <input type="text" name="firstName" onChange={this.handleChange}/>
            </div>
            <div className="field">
                <label>Last Name:</label>
                    <input name="lastName" type="text" onChange={this.handleChange}/>
            </div>
            <div className="field">
                <label>Nationality:</label>
                    <input name="nationality" type="text" onChange={this.handleChange}/>
            </div>
            <div className="field">
                <label>Team</label>
                <select>
                    {this.showTeams()}
                </select>
            </div>
            <button>Add</button>
          </form>
        );
    }
}

export default compose(
    graphql(GET_TEAMS_QUERY, {name:GET_TEAMS_QUERY}),
    graphql(CREATE_DRIVER_MUTATION, {name:CREATE_DRIVER_MUTATION}),
)(AddDriver);
