import React, { Component } from 'react';
import { graphql, compose} from 'react-apollo'
import { UPDATE_TEAM_MUTATION, GET_TEAMS_QUERY } from '../queries/queries'

class UpdateTeam extends Component {


    state = {
        name: this.props.name || "none",
        founded: this.props.name || 0
    }
   
    

    handleChange = (event) => {
        let { name, value, type} = event.target
        if (type==="number") value = Number(value)
        this.setState({
            [name]: value
        })
    }

    getTeam = (event) => {
        console.log(this.state)


        // this.setState({

        // })
    }

    showTeams() {
        let data = this.props.GET_TEAMS_QUERY
        if (data.loading) {
            return (<option disabled>Loading Teams...</option>)
        } else {
            return data.teams.map(team => {
                return (<option name="teamId" key={team.id} value={team.id}>{team.name}</option>)
            })
        }
    }

    submitForm = (event) => {
        event.preventDefault();
        this.props.UPDATE_TEAM_MUTATION({
            variables: {
                name: this.state.name,
                founded: this.state.founded
            }
            , refetchQueries: [{ query: GET_TEAMS_QUERY }]
        })
    }


    render() {
        return (
        <form onSubmit={this.submitForm}>

            <div className="field">
                <label>Team</label>
                <select onChange={(e) => { this.setState({ teamId: e.target.value }) }}>
                    {this.showTeams}
                </select>
            </div>


            <div className="field">
              <label>Team Name:</label>
              <input
                name="name"
                type="text"
                onChange={this.handleChange}
              />
            </div>
            <div className="field">
              <label>Founded:</label>
              <input
                name="founded"
                type="number"

                onChange={this.handleChange}
              />
            </div>
            <button>Add</button>
        </form>
        );
    }
}

export default compose(
    graphql(GET_TEAMS_QUERY, { name: "GET_TEAMS_QUERY" }),
    graphql(UPDATE_TEAM_MUTATION, { name: "UPDATE_TEAM_MUTATION" }),
)(UpdateTeam);
